'use strict';
var test = require('tape');
var editorConfigGetIndent = require('../');

test('get indent of this project', function t(assert) {

    var options = {
        defaultIndent: 2
    };

    editorConfigGetIndent(__dirname, options, function onIndent(err, indent) {
        if (err) {
            assert.error(err);
        }
        assert.equal(indent, 4, 'Correctly identified indent of this project');
        assert.end();
    });

});
